import { useState } from "react"
import Navigation from "./Navigation"

function Home()
{
    const [uni,setUni]=useState('');
    const [details,setDetails]=useState({
        fn:'',
        marks:''
    })

const Uni = async (event) =>
{
 setUni(event.target.value);
console.log(uni);
console.log(event.target.value);
}

const main = (event) =>
{
if(event.target.name == 'fn')
{
    setDetails(...details,details.fn=event.target.value)
}
else{
    setDetails(...details,details.marks=event.target.value)
}
}
    return (
        <div id="Home">
               <h1>CGPA to Percentage Calculator</h1>
               <select name="uni" onChange={Uni}>
                <option checked>Select</option>
                <option value='Mumbai University'>Mumbai University</option>
                <option  value='Nagpur University'>Nagpur University</option>
                <option value='Pune University'>Pune University</option>
               </select>
               <table>
                <tr>
                    <td>Enter your name</td>
                    <td><input type="text" name='fn' onChange={main}></input></td>
                </tr>
                <tr>
                    <td>CGPA</td>
                    <td><input type="number" min="0" max="10" name='marks' onChange={main} ></input></td>
                </tr>
               </table>
        </div>
    )
}
export default Home
