               </select>
               </div>


               <div class="card">

                <div class="desc">
                <h3>CGPA to Percentage Calculator</h3>
                </div>

              <div class="content">
                <t3 center>{uni}</t3>
              <table>
              <tr>
                    <td>Enter your name</td>
                    <td><input type="text" name='fn' onChange={main}></input></td>
                </tr>
                <tr>
                    <td>CGPA</td>
                    <td><input type="number" min="0" max="10" name='marks' onChange={main} ></input></td>
                </tr>
               </table>
              </div>
              <button type="button" onClick={docalculation}></button>
                <div class="output">
                  <h2>`Congragulation {details.fn} !!! You got {details.marks}</h2>
                  <h3>calculation:
                    {calculation}
                  </h3>
                </div>
               </div>

        </div>
    )
}
export default Home
