import { useState, useEffect } from "react";
import axios from "axios";
import React from "react";
import { ToastContainer, toast } from "react-toastify";

const ViewMyBooking = () => {
  const [allBookings, setAllBookings] = useState([]);
  const [count,setCount] = useState(1);
 // const [data,setData] = useState("Pay Now");

  let user = JSON.parse(sessionStorage.getItem("active-customer"));

  useEffect(() => {
    const getAllBooking = async () => {
      const allBooking = await retrieveAllBooking();
      if (allBooking) {
        setAllBookings(allBooking.bookings);
      }

    };

    getAllBooking();
  }, []);

  const retrieveAllBooking = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/book/hotel/fetch?userId=" + user.id
    );
    console.log(response.data);
    return response.data;
  };

  const paymentAlert= (id) => {
    var res=null;
    debugger;
    axios.get(
      "http://localhost:8080/api/book/hotel/fetch/booking?bookingId=" + id
    ).then((response) => {
      debugger;
       res=response.data;

            if (res.paymentStatus != "paid") {
              alert("payment done");
              // Update the payment status on the server side
              axios.post("http://localhost:8080/api/book/hotel/update/paymentstatus", {
                bookingId: id,
                paymentStatus: "paid"
              })
                .then((updateResponse) => {
                  console.log("Payment status updated:", updateResponse.data);
                var ele=document.getElementById("id");

                  // Update the payment status on the client side
                  //setPaymentStatus("paid");
                })
                .catch((error) => {
                  console.error("Error updating payment status:", error);
                });
     }

    }
    )
  }
;

  const payment = (id)=>{

    // const response = axios.get(
    //   "http://localhost:8080/api/book/hotel/fetch/id?bookingId=" + id
    // );
    // console.log(response);
    // return response.data;

    if(count==1){
        toast.success("Payment successfull!!!", {
          position: "top-center",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });

       // setData("Paid");
        const ele=document.getElementById(id)
        ele.innerText="Paid"
        if(ele.innerText=="Paid"){
          ele.closeOnClick=true;
        }
       setCount(count+1);
      }
  }

  return (
    <div className="mt-3">
      <div
        className="card form-card ms-2 me-2 mb-5 custom-bg border-color "
        style={{
          height: "45rem",
        }}
      >
        <div className="card-header custom-bg-text text-center bg-color">
          <h2>My Bookings</h2>
        </div>
        <div
          className="card-body"
          style={{
            overflowY: "auto",
          }}
        >
          <div className="table-responsive">
            <table className="table table-hover text-color text-center">
              <thead className="table-bordered border-color bg-color custom-bg-text">
                <tr>
                  <th scope="col">Hotel</th>
                  <th scope="col">Hotel Name</th>
                  <th scope="col">Hotel Email</th>
                  <th scope="col">Hotel Contact</th>
                  <th scope="col">Booking Id</th>
                  <th scope="col">Customer Name</th>
                  <th scope="col">Customer Contact</th>
                  <th scope="col">Check In</th>
                  <th scope="col">Check Out</th>
                  <th scope="col">Total Room</th>
                  <th scope="col">Total Day</th>
                  <th scope="col">Booking Status</th>
                  <th scope="col">Total Payable Amount</th>
                  <th scope="col" >Payment</th>
                </tr>
              </thead>
              <tbody>
                {allBookings.map((booking) => {
                  return (
                    <tr>
                      <td>
                        <img
                          src={
                            "http://localhost:8080/api/hotel/" +
                            booking.hotelImage
                          }
                          class="img-fluid"
                          alt="product_pic"
                          style={{
                            maxWidth: "90px",
                          }}
                        />
                      </td>

                      <td>
                        <b>{booking.hotelName}</b>
                      </td>
                      <td>
                        <b>{booking.hotelEmail}</b>
                      </td>
                      <td>
                        <b>{booking.hotelContact}</b>
                      </td>

                      <td>
                        <b>{booking.bookingId}</b>
                      </td>
                      <td>
                        <b>{booking.customerName}</b>
                      </td>
                      <td>
                        <b>{booking.customerContact}</b>
                      </td>

                      <td>
                        <b>{booking.checkIn}</b>
                      </td>
                      <td>
                        <b>{booking.checkOut}</b>
                      </td>
                      <td>
                        <b>{booking.totalRoom}</b>
                      </td>

                      <td>
                        <b>{booking.totalDay}</b>
                      </td>
                      <td>
                        <b>{booking.status}</b>
                      </td>
                      <td>
                        <b>{booking.totalAmount}</b>
                      </td>
                      <td>
                        {(() => {
                          if (booking.status == "Approved") {
                            return (
                              <button id={booking.bookingId}
                                onClick={() => payment(booking.bookingId)}
                                className="btn btn-sm bg-color custom-bg-text"
                              >
                                Pay Now
                                {/* {data} */}
                              </button>
                            );
                          }
                        })()}
                       <ToastContainer />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewMyBooking;
